	(4606,626,'activities_0_activity_name','Introductions and objective setting; Intro from Myddleton Communications'),
	(4607,626,'_activities_0_activity_name','field_5b11097814d45'),
	(4608,626,'activities_0_activity_description','The Bladonmore team will introduce themselves and set some context for the session. We will welcome the Myddleton team to comment about the nature of the work that we do and that the purpose of the coaching is to arm the participants with the techniques and confidence to take advantage of media opportunities that will enable them to promote their own practices and the firms brand, and also to take on active competitors in their market.'),
	(4609,626,'_activities_0_activity_description','field_5b11098d14d46'),
	(4610,626,'activities_0_activity_start','13:00:00'),
